import React, { useState } from 'react';



// import { ethers } from 'ethers';


function App() {

// const imgdir = FileSystem.documentDirectory + 'images/';

// const ensureDirExist = async () => {
//   const dirInfo = await FileSystem.getInfoAsync(imgdir);
//   if (!dirInfo.exists) {
//     await FileSystem.makeDirectoryAsync(imgdir, { intermediates: true });
//   }
// };
  // const [img, setImg] = useState(null);
  const [connectedAddress, setConnectedAddress] = useState(null);
  const [file,setFile] = useState("")
  const [fileURI, setFileURI] = useState(null);
  // useEffect(() => {
  //   ensureDirExist();
  // }, []);

 const connectMetamask = async() =>{

  try {
    if(window.ethereum){
    const accounts = await window.ethereum.request({method:"eth_requestAccounts"});
    console.log(window.ethereum)
    const account = accounts[0]
    setConnectedAddress(account)

  }
  } catch (error) {
    console.error(error)

  }
 }

 const fileChange = async(event) =>{

      const selectedFile = event.target.files[0] // that files here is a attribute withj the input tages that shows an array of files and we pulling uuo tghg first one 

      console.log(selectedFile)
      setFile(selectedFile)

      const uri = URL.createObjectURL(selectedFile) // what does this do *update
      setFileURI(uri)
      console.log(uri)

 }

  return (
    <div>
      <button onClick={connectMetamask}> Connect Wallet </button>
      {/* <View style={styles.container}> */}

      <p>{connectedAddress}</p>

      {/* <StatusBar style="auto" />
    </View> */}
      <input
          type="file"
          onChange={fileChange}
          />
      <p>{file.name}</p>
          {/* *after the 
          file is uploaded I want to update the file state with the url or uri of the photo uploaded and later I want to send that url to ipfs 
    </div> */}

      {fileURI && <p>file URI: {fileURI}</p>}
    </div>

  );
};

export default App;
