import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [connectedAddress, setConnectedAddress] = useState(null);
  const [ipfsHash, setIpfsHash] = useState(null);

  const connectMetamask = async () => {
    try {
      if (window.ethereum) {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        const account = accounts[0];
        setConnectedAddress(account);
      }
    } catch (error) {
      console.error(error);
    }
  };

  const fileChange = async (event) => {
    const selectedFile = event.target.files[0];

    try {
      const formData = new FormData();
      formData.append('file', selectedFile);

      // Upload the file to NFT.storage
      const response = await axios.post('https://api.nft.storage/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiJkaWQ6ZXRocjoweGQ0MDhGYzI5NDc4RGM4OEFkYUE0NzdGNzE3NGFFNmQ5RWZEMzZiYTkiLCJpc3MiOiJuZnQtc3RvcmFnZSIsImlhdCI6MTcwODQ4MDk0ODUwOCwibmFtZSI6InNuYXRjaCJ9.jsK4LWYwQrAp3wiGtke4e2yul2nT7dufyZnyRHa3ad8', // Replace with your NFT.storage API key
        },
      });

      // Get the IPFS hash from the response
      const ipfsHash = response.data.value.cid;
      setIpfsHash(ipfsHash);

    } catch (error) {
      console.error('Error uploading to NFT.storage:', error);
    }
  };

  return (
    <div>
      <button onClick={connectMetamask}>Connect Wallet</button>
      <p>{connectedAddress}</p>

      <input
        type="file"
        onChange={fileChange}
      />
      <p>File IPFS Hash: {ipfsHash}</p>
       <img src={`https://ipfs.io/ipfs/${ipfsHash}`} alt="IPFS" />
    </div>
  );
}

export default App;
