/* eslint-disable-next-line */
import React, { useState } from 'react';
import { create } from 'ipfs-http-client';

function App() {
  const [connectedAddress, setConnectedAddress] = useState(null);
  const [file, setFile] = useState("");
  const [ipfsHash, setIpfsHash] = useState(null);

  const connectMetamask = async () => {
    try {
      if (window.ethereum) {
        const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
        console.log(window.ethereum);
        const account = accounts[0];
        setConnectedAddress(account);
      }
    } catch (error) {
      console.error(error);
    }
  }

  const fileChange = async (event) => {
    const selectedFile = event.target.files[0];

    try {
      // Connect to IPFS node
      const ipfsNode = create({ host: "localhost", port: 5001, protocol: "http" });

      setFile(selectedFile);

      // Upload the file to IPFS and get the content hash
      const result = await ipfsNode.add(selectedFile);
      const hash = result.cid.toString();

      setIpfsHash(hash);

      const uri = URL.createObjectURL(selectedFile);
      console.log(uri);

    } catch (error) {
      console.error(error);
    }
  }

  return (
    <div>
      <button onClick={connectMetamask}> Connect Wallet </button>
      <p>{connectedAddress}</p>

      <input
        type="file"
        onChange={fileChange}
      />
      <p>{file.name}</p>

      <p>File URI: {ipfsHash}</p>
    </div>
  );
}

export default App;
